%spark2

// Print the schema in a tree format
flights.printSchema()

%spark2

// Show a subset of columns with "select"
flights.select("UniqueCarrier", "FlightNum", "DepDelay", "ArrDelay", "Distance").show()

%spark2

import org.apache.spark.sql.functions.udf

// Define a UDF to find delayed flights

// Assume:
//  if ArrDelay is not available then Delayed = False
//  if ArrDelay > 15 min then Delayed = True else False

val isDelayedUDF = udf((time: String) => if (time == "NA") 0 else if (time.toInt > 15) 1 else 0)

%spark2

// Convert flights DataFrame to a temporary view
flights.createOrReplaceTempView("flightsView")

%spark2

// Register a helper UDF to find delayed flights
// Note that this is a UDF specific for use with the sparkSession

// Assume:
//  if ArrDelay is not available then Delayed = False
//  if ArrDelay > 15 min then Delayed = True else False

spark.udf.register("isDelayedUDF", (time: String) => if (time == "NA") 0 else if (time.toInt > 15) 1 else 0)

%spark2

// Load results back from ORC file
val test = spark.read.format("orc").load("flightsWithDelays.orc")

// Assert both DataFrames of the same size.
//   Note that if assertion succeeds no warning messages will be printed
assert (test.count == flightsWithDelays.count, println("Assertion Fail: Files are of different sizes."))

test.show(10)
